class Accordion{constructor(t){this.title=document.querySelectorAll(t)}single(){this.title.forEach(t=>{t.addEventListener("click",function(){this.classList.toggle("open"),this.nextElementSibling.classList.toggle("show")})})}static closeAll(){this.title.forEach(t=>{console.log(this.title),console.log(t)})}multiply(){this.title.forEach(t=>{const e=this.title;t.addEventListener("click",function(){e.forEach(t=>{t.classList.remove("open"),t.nextElementSibling.classList.remove("show")}),this.classList.toggle("open"),this.nextElementSibling.classList.toggle("show")})})}}

document.querySelectorAll(".levus-accordion .title")&&new Accordion(".levus-accordion .title").multiply();
